import React from 'react';

function App() {
  return (
    function Example() {
      // Declara uma nova variável de state, que chamaremos de "count"
      const [count, setCount] = useState(0);

      return (
        <div>
          <p>You clicked {count} times</p>
          <button onClick={() => setCount(count + 1)}>
            Click me
          </button>
        </div>
      );
    }
  );
}

export default App;
